var fc = 16; // Function code for Write Multiple Registers
var sa = 40149; // Starting address of the Modbus register
var quantity = 4; // Number of contiguous registers to write (2 registers for 1 32-bit integer)

// Retrieve the changing value from the payload
var value = msg.payload;

// If the value is valid, proceed with conversion and payload construction
if (value !== undefined && !isNaN(value)) {
    // Convert the value to a signed 32-bit integer for Modbus register
    var intValue = Math.round(value); // Assuming input is already in S32 format
    var buf = Buffer.alloc(4); // Use 4 bytes for 1 32-bit integer

    // Write the signed 32-bit integer value
    buf.writeInt32BE(intValue, 0); // Write the integer at the start of the buffer

    // Construct the final message payload
    msg.payload = {
        value: buf,
        'fc': fc,
        'unitid': 1, // Modbus unit ID
        'address': sa,
        'quantity': quantity, // Number of registers to write (2 registers for 1 32-bit integer)
        'access': 'WO' // Access type (Write Only)
    };
} else {
    // If the value is invalid, log an error message
    console.error("Invalid value:", value);
}

return msg;

